<template>
  <div id="container">
    <div id="cart">
      <!-- 预加载 -->
      <div class="scroll-loading" v-if="isLoading">
        <img src="https://s1.ricebook.com/feck/web-order/b380c4db1ab8094f087c772351ece4de.gif">
      </div>
      <!-- 内容 -->
      <div v-if="!isLoading">
        <!-- 列表 -->
        <div class="cart-group" v-for="(item,index) in list" :key="index">
          <ul class="cart-list">
            <li class="clearfix">
              <div class="sel-wrapper clearfix">
                <span
                  class="sel iconfont"
                  @click="dealSelectGoods(index)"
                  :class="{on:item.selected==true}"
                ></span>
              </div>
              <div class="desc clearfix">
                <div class="image">
                  <img :src="item.product_image">
                </div>
                <div class="info">
                  <p class="title">{{item.short_name}}</p>
                  <p class="cate">{{item.spec_name}}: {{item.spec}}</p>
                  <p class="price">单价: {{item.price|deleteZero}}元</p>
                  <div class="opt">
                    <div class="operate">
                      <a
                        class="subtract"
                        @click="dealSub(index)"
                        :class="{disable:item.selected_count<=1}"
                      >-</a>
                      <span class="num">{{item.selected_count}}</span>
                      <a
                        class="plus"
                        @click="dealAdd(index)"
                        :class="{disable:item.selected_count>=9}"
                      >+</a>
                    </div>
                  </div>
                  <a @click="dealDelete(index)" class="remove iconfont"></a>
                </div>
              </div>
            </li>
          </ul>
          <div class="panel-gap"></div>
        </div>
        <!-- 购物车底部 -->
        <div class="cart-footer">
          <div class="cart-pay">
            <a class="sel-all">
              <span @click="dealSelectAll" class="sel iconfont" :class="{on:isSelectAll==true}"></span>
              <span class="text">全选</span>
            </a>
            <a class="pay-go">去结算</a>
            <div class="pay-content">
              合计：
              <span>{{allPrice}}</span> 元
            </div>
          </div>
        </div>
      </div>
    </div>
  </div>
</template>

<script>
export default {
  name: "cart",
  data() {
    return {
      isLoading: true,
      list: [], //购物车数据
      isSelectAll: true //是否全选
    };
  },

  created() {
    //下载购物车数据
    this.request.getCart({}, data => {
      //console.log(data)
      this.list = data.product_list;
      //console.log(this.list);
      this.isLoading = false;
    });
  },
  mounted() {},
  methods: {
    //数量-
    dealSub(index) {
      var goods = this.list[index];
      if (goods.selected_count - 1 > 0) {
        goods.selected_count--;
      }
    },
    //数量+
    dealAdd(index) {
      var goods = this.list[index];
      if (goods.selected_count < 9) {
        goods.selected_count++;
      }
    },
    //删除
    dealDelete(index) {
      this.list.splice(index, 1);
    },
    //商品是否全部选中
    dealSelectGoods(index) {
      var goods = this.list[index];
      goods.selected = !goods.selected;

      //判断所有商铺是否被选中
      var isSelectAllGoods = true;
      for (var i = 0; i < this.list.length; i++) {
        if (this.list[i].selected == false) {
          isSelectAllGoods = false;
        }
      }

      if (isSelectAllGoods) {
        this.isSelectAll = true;
      } else {
        this.isSelectAll = false;
      }
    },
    //是否全选
    dealSelectAll(index) {
      this.isSelectAll = !this.isSelectAll;

      var list = this.list;
      for (var i = 0; i < list.length; i++) {
        list[i].selected = this.isSelectAll;
      }
    }
  },
  computed: {
    allPrice() {
      var allPrice = 0;
      var list = this.list;
      for (var i = 0; i < list.length; i++) {
        if (list[i].selected) {
          allPrice += list[i].selected_count * (list[i].price / 100);
        }
      }
      return number_format(allPrice, 2, ".", ",");
    }
  },
  watch: {},
  components: {}
};
</script>

